import React, { useEffect, useState } from 'react';
import { fetchDockerStats } from '../api';

function DockerStatsTable() {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDockerStats()
      .then((res) => {
        setStats(res.stats);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  return (
    <section className="container my-4">
      <div className="card shadow-lg">
        <div className="card-header bg-success text-white text-center">
          <h2 className="mb-0">Recursos Docker en Tiempo Real</h2>
        </div>
        <div className="card-body">
          {loading ? (
            <div className="text-center py-4">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando...</p>
            </div>
          ) : error ? (
            <div className="alert alert-danger text-center">Error: {error}</div>
          ) : !stats ? (
            <div className="alert alert-warning text-center">No hay datos</div>
          ) : (
            <table className="table table-striped table-bordered align-middle text-center">
              <thead className="table-success">
                <tr>
                  <th>Contenedor</th>
                  <th>CPU (%)</th>
                  <th>Memoria</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(stats).map(([name, val]) => (
                  <tr key={name}>
                    <td>{name}</td>
                    <td>{val.cpu}</td>
                    <td>{val.mem}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </section>
  );
}

export default DockerStatsTable;
