import React, { useEffect, useState } from 'react';

function formatBytes(bytes) {
  if (!bytes && bytes !== 0) return 'N/A';
  const units = ['B','KB','MB','GB','TB'];
  let i = 0;
  let v = bytes;
  while (v >= 1024 && i < units.length - 1) { v /= 1024; i++; }
  return `${v.toFixed(2)} ${units[i]}`;
}

function DbSizesMonitor({ intervalMs = 0, pollMs = 0 }) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function fetchOnce() {
    try {
      setLoading(true);
      setError(null);
      const q = intervalMs > 0 ? `?intervalMs=${intervalMs}` : '';
      const res = await fetch(`/api/monitor/db-sizes${q}`);
      if (!res.ok) throw new Error(await res.text());
      const json = await res.json();
      setData(json);
    } catch (e) {
      setError(String(e));
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchOnce();
    if (pollMs > 0) {
      const t = setInterval(fetchOnce, pollMs);
      return () => clearInterval(t);
    }
  }, [intervalMs, pollMs]);

  return (
    <section className="container my-3">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="mb-0">Monitor DB: tamaños</h5>
        <div>
          <button className="btn btn-sm btn-outline-primary me-2" onClick={fetchOnce}>Actualizar</button>
          <small className="text-muted">intervalMs: {intervalMs}ms</small>
        </div>
      </div>

      {loading && <div className="alert alert-info">Obteniendo métricas...</div>}
      {error && <div className="alert alert-danger">Error: {error}</div>}

      {data && (
        <div className="row g-3">
          <div className="col-md-6">
            <div className="card shadow-sm">
              <div className="card-header bg-warning text-dark">MonetDB</div>
              <div className="card-body">
                <p className="mb-1"><strong>Container:</strong> {data.monet.container}</p>
                <p className="mb-1"><strong>Path:</strong> {data.monet.path}</p>
                <p className="mb-1"><strong>Tamaño:</strong> {formatBytes(data.monet.bytes)} ({data.monet.bytes} bytes)</p>
                <p className="mb-0"><strong>Delta:</strong> {formatBytes(data.monet.deltaBytes)} ({data.monet.deltaBytes} bytes)</p>
              </div>
            </div>
          </div>

          <div className="col-md-6">
            <div className="card shadow-sm">
              <div className="card-header bg-info text-white">PostgreSQL</div>
              <div className="card-body">
                {data.postgres && data.postgres.length > 0 ? (
                  <table className="table table-sm">
                    <thead><tr><th>BD</th><th>Tamaño</th><th>Delta</th></tr></thead>
                    <tbody>
                      {data.postgres.map(p => (
                        <tr key={p.datname}>
                          <td>{p.datname}</td>
                          <td>{formatBytes(p.bytes)} <small className="text-muted">({p.bytes})</small></td>
                          <td>{formatBytes(p.deltaBytes)} <small className="text-muted">({p.deltaBytes})</small></td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : <div className="text-muted">No hay datos de Postgres</div>}
              </div>
            </div>
          </div>
        </div>
      )}
    </section>
  );
}

export default DbSizesMonitor;
