// Puedes poner esto en cualquier componente, por ejemplo src/components/FlightActions.js
import React, { useState } from 'react';
import { addFlights, removeFlights } from '../api';

export default function FlightActions() {
  const [count, setCount] = useState(100);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleAdd = async () => {
    setLoading(true);
    try {
      const res = await addFlights(count);
      setResult(res);
    } catch (e) {
      setResult({ error: e.message });
    }
    setLoading(false);
  };

  const handleRemove = async () => {
    setLoading(true);
    try {
      const res = await removeFlights(count);
      setResult(res);
    } catch (e) {
      setResult({ error: e.message });
    }
    setLoading(false);
  };

  return (
    <div>
      <h3>Acciones de Vuelos</h3>
      <input
        type="number"
        value={count}
        min={1}
        onChange={e => setCount(Number(e.target.value))}
        style={{ marginRight: 8 }}
      />
      <button onClick={handleAdd} disabled={loading}>Agregar vuelos</button>
      <button onClick={handleRemove} disabled={loading}>Eliminar vuelos</button>
      {loading && <p>Cargando...</p>}
      {result && (
        <pre>{JSON.stringify(result, null, 2)}</pre>
      )}
    </div>
  );
}
