import React, { useState } from 'react';
import { addFlights, removeFlights } from '../api';

export default function FlightActions() {
  const [count, setCount] = useState(100);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleAdd = async () => {
    setLoading(true);
    try {
      const res = await addFlights(count);
      setResult(res);
    } catch (e) {
      setResult({ error: e.message });
    }
    setLoading(false);
  };

  const handleRemove = async () => {
    setLoading(true);
    try {
      const res = await removeFlights(count);
      setResult(res);
    } catch (e) {
      setResult({ error: e.message });
    }
    setLoading(false);
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginTop: 40,
      marginBottom: 40
    }}>
      <h3 style={{ marginBottom: 16 }}>Acciones de Vuelos</h3>
      <div style={{
        display: 'flex',
        gap: 12,
        alignItems: 'center',
        marginBottom: 16
      }}>
        <input
          type="number"
          value={count}
          min={1}
          onChange={e => setCount(Number(e.target.value))}
          style={{
            padding: '6px 12px',
            borderRadius: 6,
            border: '1px solid #ccc',
            fontSize: 16,
            width: 80
          }}
        />
        <button
          onClick={handleAdd}
          disabled={loading}
          style={{
            padding: '8px 18px',
            borderRadius: 6,
            border: 'none',
            background: '#1976d2',
            color: '#fff',
            fontWeight: 'bold',
            fontSize: 16,
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'background 0.2s'
          }}
        >
          Agregar vuelos
        </button>
        <button
          onClick={handleRemove}
          disabled={loading}
          style={{
            padding: '8px 18px',
            borderRadius: 6,
            border: 'none',
            background: '#d32f2f',
            color: '#fff',
            fontWeight: 'bold',
            fontSize: 16,
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'background 0.2s'
          }}
        >
          Eliminar vuelos
        </button>
      </div>
      {loading && <p style={{ color: '#1976d2' }}>Cargando...</p>}
      {result && (
        <pre style={{
          background: '#f5f5f5',
          padding: 12,
          borderRadius: 6,
          maxWidth: 400,
          marginTop: 8,
          fontSize: 14
        }}>{JSON.stringify(result, null, 2)}</pre>
      )}
    </div>
  );
}
