import { useEffect, useState } from 'react';
import { fetchQuerySql } from '../api';

export default function useQuerySql(name) {
  const [sqls, setSqls] = useState(null);
  useEffect(() => {
    let mounted = true;
    if (!name) return;
    fetchQuerySql(name)
      .then(r => {
        if (!mounted) return;
        if (r && r.ok) setSqls({ pg: r.pg, monet: r.monet });
        else setSqls(null);
      })
      .catch(err => {
        console.error('useQuerySql error', name, err);
        if (mounted) setSqls(null);
      });
    return () => { mounted = false; };
  }, [name]);
  return sqls;
}
